class Solution {
public:
    int lastStoneWeight(vector<int>& stones) {
        priority_queue<int> maxheap;
        for(auto elm: stones)
        {
            maxheap.push(elm);
        }
        while(true)
        {
            if(maxheap.size()==1)
            return maxheap.top();
            if(maxheap.size()==0)
            return 0;
            int first=maxheap.top();
            maxheap.pop();
            int second= maxheap.top();
            maxheap.pop();
            if(first!=second)
            {
                maxheap.push((first-second));

            }
        }
    }
};
