class Solution {
public:
    int lastStoneWeight(std::vector<int>& stones) {
        std::sort(stones.rbegin(), stones.rend());
        while (stones.size() > 1) {
            int y = stones[0]; 
            int x = stones[1];
            stones.erase(stones.begin(), stones.begin()+2);
            if (x != y) {
                stones.insert(std::upper_bound(stones.begin(), stones.end(), y-x, std::greater<int>()), y-x);
            }
        }
        return stones.empty() ? 0 : stones[0];
    }
};

```

