                start+=1;
                continue;
            }
            boolean flag = true;                // brute force
            for (int j = i+1; j < m; j++){
                if (isValid(grid,start,r[j])){
                    flag = false;
                    ans += swap(r,i,j);
                    break;
                }
            }
            if (flag) return -1;
            start++;
        }
        return ans; 
    }
    boolean isValid(int[][] grid, int start, int row){
        for (int i = start; i < grid[0].length; i++){
            if (grid[row][i] == 1) return false;
        }
        return true;
    }
    int swap(int[] r, int start, int end){
        int cnt = 0;
        for (int i = end; i >= start+1; i--){
            int tmp = r[i];
            r[i] = r[i-1];
            r[i-1] = tmp;
            cnt +=1;
        }
        return cnt;
    }
