class Solution {
public:
    int minSwaps(vector<vector<int>>& grid) {
        int n = grid.size();
        vector<int> zero_count;
        for (const auto &each : grid) {
            int count = 0;
            for (int i = n - 1; i >= 0; --i) {
                if (each[i]) break;
                count += 1;
            }
            zero_count.push_back(count);
        }
        int moves = 0;
        for (int i = 0; i < n; ++i) {
            if (zero_count[i] < n - 1 - i) {
                int end = i;
                while (end < n && zero_count[end] < n - 1 - i) {
                    ++end;
                }
                if (end == n) return -1;
                while (end > i) {
                    swap(zero_count[end], zero_count[end-1]);
                    --end;
                    ++moves;
                }
            }
        }
        return moves;
    }
};
