    public int minSwaps(int[][] g) {
        List<Integer> l = new LinkedList<>();
        int m = g.length, n = g[0].length, res = 0;
        for (int i = 0, z = 0; i < m; i++, z = 0) {// z trailing zeros
            for (int j = n - 1; j >= 0 && g[i][j] == 0; j--) z++;
            l.add(z);
        }
        for (int i = 0, j = 0, z = m - 1; i < m; i++, z--, j = i) {
            while (l.get(j) < z) if (++j >= n) return -1;
            l.remove(j);
            l.add(i, z);
            res += j - i;
        }
        return res;
    }
