	public int minSwaps(int[][] grid) {
        int n = grid.length, A[] = new int[n], res = 0;

        // A[i] - number of zeros at end of ith row = grid[i]
        for (int i = 0; i < n; i++) 
            for (int j = n-1; j >= 0 && grid[i][j] == 0; j--) 
                A[i]++;                

        // sort by descending the number of zeros
        // to be above the main diagonal are zeros: A[i] >= n-i-1;
        for (int i = 0; i < n; i++) {

            // above diagonal itself
            if (A[i] >= n-i-1) continue;

            // under diagonal we search j: A[j] >= n-i-1
            int j = i+1;
            while (j < n && A[j] < n-i-1) j++;

            // if not found
            if (j == n) return -1;

            // if found j: swap 2 adjacent rows
            while (j > i) {
                swap(A, j-1, j--);
                res++;
            }
        } 

        return res;                
    }

    private void swap(int[] A, int i, int j) {
        int tmp = A[i]; A[i] = A[j]; A[j] = tmp;
    }
