        for(int i = grid.size() - 1; i > 0; --i) t.push_back(i);
        for(int i = 0; i < t.size(); ++i) {
            int a = t[i];
            bool f = false;
            for(int j = i; j < src.size(); ++j) {
                if(src[j] >= a) {
                    src.erase(src.begin() + j);
                    src.insert(src.begin() + i, a);
                    ans += j - i;
                    f = true;
                    break;
                }
            }
            if(!f) return -1;
        }
        return ans;
    }
};

