class Solution {
    public int minSwaps(int[][] grid) {
        int n = grid.length;
        int[] cnt = new int[n];     // Counter for trailing 0s
        for(int i = 0; i < n; i++)
            for(int j = n - 1; j >= 0 && grid[i][j] == 0; j--)
                cnt[i] += 1;

        int ans = 0;
        for(int i = 0; i < n - 1; i++){
            // If current row doesn't have enough 0
            if(cnt[i] < n - 1 - i){
                // Try to find a row below which has enough 0s with minimum times of swap
                for(int j = i + 1; j < n; j++){
                    // If find one row with enough 0s
                    if(cnt[j] >= n - 1 - i){
                        ans += j - i;
                        int tmp = cnt[i];
                        // swap it to cnt[i]
                        cnt[i] = cnt[j];
                        // push cnt[i, j - 1] one position forward
                        for(int k = j; k > i + 1; k--)
                            cnt[k] = cnt[k - 1];
                        cnt[i + 1] = tmp;
                        break;
                    }
                    // Can't find a row with enough 0s, return -1
                    if(j >= n - 1)
                        return -1;
                }
            }
        }
        return ans;
    }
}
