class Solution {
    public int minSwaps(int[][] grid) {
        int n = grid.length;
        int[] arr = new int[n];
        for (int i = 0; i < n; i++) {
            for (int j = n-1; j >= 0; j--) {
                if (grid[i][j] == 1) {
                    break;
                }
                arr[i]++;
            }
        }

        int swaps = 0;
        int i = 0;
        for (int search = n-1; search >= 0; search--) {
            int j = i;
            while (arr[j] < search) {
                j++;
                if (j == n) {
                    return -1;
                }
            }
            while (j > i) {
                int tmp = arr[j];
                arr[j] = arr[j-1];
                arr[j-1] = tmp;
                swaps++;
                j--;
            }
            i++;
        }
        return swaps;
    }
}
