class Solution {
public:
    int minSwaps(vector<vector<int>>& grid) {
        int n = grid.size();
        vector<int> max_right;
        for (auto g : grid) {
            int right = 0;
            for (int i = 0; i < n; ++i) {
                if (g[i])
                    right = max(right, i);
            }
            max_right.push_back(right);
        }

        auto check = max_right;
        sort(check.begin(), check.end());
        for (int i = 0; i < n; ++i) {
            if (check[i] > i)
                return -1;
        }

        int swaps = 0;
        for (int i = 0; i < n; ++i) {

            int idx = -1;
            for (int j = i; j < n; ++j) {
                if (max_right[j] <= i) {
                    idx = j;
                    break;
                }
            }
            for (int j = idx; j > i; --j) {
                max_right[j] = max_right[j - 1];
                ++swaps;
            }
        }
        return swaps;
    }
};
