class Solution {
public:
    int minSwaps(vector<vector<int>>& grid) {
        int n = grid.size();

        vector<int> zeroCounts(n, 0);                  //calc ending zero count of each row
        for (int i = 0; i < n; ++i)
            for (int j =n - 1; j >= 0 && grid[i][j] == 0; --j, ++zeroCounts[i]);

        int steps = 0,i=0,j=0;
        for ( ; i < n; ++i) {                           //try to match each row (ending zero count euqal to i-1)
            if (zeroCounts[i] >= n - i - 1) continue;   //already matched

            for ( j = i + 1; j < n; ++j)                //find out a correct row to switch with current row
                if (zeroCounts[j] >= n - i - 1) 
                {
                    for (int k = j; k > i; --k) { 
                        swap(grid[k], grid[k - 1]);
                        swap(zeroCounts[k], zeroCounts[k - 1]);
                    }

                    steps += j - i;
                    break;
                }

            if (j == n) return -1;
        }

        return steps;
    }
};
