class Solution {
    public int minSwaps(int[][] grid) {
        int n = grid.length, ans = 0;
        for (int i = 0; i<n; i++) {
            if (valid(grid[i], i)) continue;
            boolean found = false;
            for (int j = i+1; j<n; j++) {
                if (valid(grid[j], i)) {
                    // swap row i and j
                    grid = swap(grid, i, j);
                    ans += j-i;
                    found = true;
                    break;
                }
            }
            if (!found) return -1;
        }
        return ans;
    }
    public int[][] swap(int[][] grid, int i, int j) {
        int [] j_row = grid[j].clone();
        for (int k = j-1; k>=i; k--) {
            grid[k+1] = grid[k];
        }
        grid[i] = j_row;
        //System.out.println(Arrays.deepToString(grid));
        return grid;
    }
    public boolean valid(int[] r, int idx) {
        for (int i = idx+1; i<r.length; i++) {
            if (r[i]==1) return false;
        }
        return true;
    }

}
