class Solution {
    public int minSwaps(int[][] grid) {
        //strategy: Greedy
        int n = grid.length, answer = 0;
        //calculate the length of the trailing zeroes
        ArrayList<Integer> trailing = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            int count = 0;
            //go from the back until we reach a one
            for (int j = n - 1; j >= 0; j--) {
                if (grid[i][j] == 1) {
                    break;
                } else {
                    count++;
                }
            }
            //add to ArrayList
            trailing.add(count);
        }
        for (int i = 0; i < n; i++) {
            //this is the needed amt to curr row valid
            int needed = n - i - 1;
            boolean flag = false;
            //only needed to check ith row onwards because we already set up the first ith rows
            for (int j = i; j < n; j++) {
                //find the closest row
                if (trailing.get(j) >= needed) {
                    //add to swaps
                    answer += j - i;
                    //simulate swaps
                    trailing.add(i, trailing.remove(j));
                    flag = true;
                    break;
                }
            }
            //didn't find it
            if (!flag) {
                return -1;
            }
        }
        return answer;
    }
}
