class Solution(object):
    def minSwaps(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        res = 0
        n = len(grid)
        zeroCount = self.getZeroCount(grid)
        for i in range(len(grid)):
            if zeroCount[i] < n-i-1:
                j = i
                while j < n and zeroCount[j] < n - i - 1:
                    j += 1
                if j == n:
                    return -1
                while j > i:
                    zeroCount[j], zeroCount[j-1] = zeroCount[j-1], zeroCount[j]
                    j -= 1
                    res += 1

        return res

    def getZeroCount(self, grid):
        zeroCount = []
        m, n = len(grid), len(grid[0])
        for i in range(m):
            count = 0
            for j in range(n-1, -1, -1):
                if grid[i][j] == 0:
                    count+=1
                else:
                    break
            zeroCount.append(count)

        return zeroCount


