    def minSwaps(self, grid: List[List[int]]) -> int:
        count, size, rowN, tag, has = 0, len(grid), 0, 0, {}
        tmpCount = 0
        while rowN < size-1 and tag < size:
            if tag in has.keys():
                tag += 1
                tmpCount += 1
                continue
            if 1 not in grid[tag][rowN+1:]:
                count += tag - tmpCount
                rowN += 1
                has[tag] = tag
                tag, tmpCount = 0,0
            else:
                tag += 1
        return count if rowN == size-1 else -1
