class Solution {
    public int minSwaps(int[][] grid) {
        if (grid == null || grid.length == 0) {
            return -1;
        }
        int m = grid.length;
        int n = grid[0].length;
        int[] counts = new int[m];
        PriorityQueue<Integer> pq = new PriorityQueue<>((x, y) -> counts[x] == counts[y] ? x - y : counts[y] - counts[x]);
        TreeSet<Integer> set = new TreeSet<>();
        for (int i = 0; i < m; i++) {
            counts[i] = count(grid[i]);
            set.add(i);
        }
        for (int i = 0; i < m; i++) {
            pq.add(i);
        }
        PriorityQueue<Integer> rank = new PriorityQueue<>();
        int result = 0;
        for (int i = n - 1; i >= 0; i--) {
            while (!pq.isEmpty() && counts[pq.peek()] >= i) {
                rank.add(pq.poll());
            }
            if (rank.isEmpty()) {
                return -1;
            }
            int selectedRow = rank.poll();
            result += set.headSet(selectedRow).size();
            set.remove(selectedRow);
        }
        return result;
    }

    private int count(int[] row) {
        int count = 0;
        for (int i = row.length - 1; i >= 0; i--) {
            if (row[i] == 0) {
                count++;
            } else {
                return count;
            }
        }
        return count;
    }
}
