class Solution:
    def minSwaps(self, grid: List[List[int]]) -> int:
        arr, N = [], len(grid)
        if N == 1:
            return -1 if grid[0][0] else 0

        issorted = True
        for i in range(N):
            zeroes = 0
            for j in range(N-1, -1, -1):
                if grid[i][j]: 
                    break
                zeroes += 1
            arr.append(zeroes)
            if len(arr) > 1 and issorted:
                issorted = arr[-2] > arr[-1]

        if issorted:
            return 0

        swaps = 0
        for i in range(N - 1, -1, -1):
            lans = 0
            for j in range(N):
                if arr[j] is not None and arr[j] >= i:
                    arr[j] = None
                    swaps += lans
