int minSwaps(vector<vector<int>>& grid){
    int ans = 0;
    int n = grid.size();
    if (!n) return 0;
    vector<int> vec(n, -1);
    for (int i = 0; i < n; ++i) {
        int count = 0;
        for (int j = n - 1; j >= 0; --j) {
            if (grid[i][j] == 1) break;
            ++count;
        }
        vec[i] = count;
    }

    int limit = n - 1;
    while (limit) {
        int count = 0;
        bool flag = false;
        for (int i = 0; i < n; ++i) {
            if (vec[i] >= limit) {
                ans += count;
                vec[i] = -1;
                flag = true;
                break;
            } else if (vec[i] >= 0) {
                ++count;
            }
        }
        if (!flag) return -1;
        limit--;
    }
    return ans;
}
