class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        // System.out.println(servers.length);
        // System.out.println(servers[36] + " ; " + servers[56]);
        int N = tasks.length;
        int[] res = new int[N];
        PriorityQueue<int[]> available = new PriorityQueue<>((s1, s2) -> {
            if (s1[1] == s2[1]) return s1[0] - s2[0];
            return s1[1] - s2[1];
        }); // (index, weight), sort by weight first then index, increasingly
        PriorityQueue<int[]> running = new PriorityQueue<>((s1, s2) -> s1[1] - s2[1]); // (index, finishing time), sort by finishing time increasingly

        for (int i = 0; i < servers.length; i++) {
            available.add(new int[] {i, servers[i]});
        }
        int i = 0;
        int time = 0;
        while (i < N) {
            while (!running.isEmpty() && running.peek()[1] <= time) {
                int[] freeServer = running.poll();
                int index = freeServer[0];
                available.offer(new int[] {index, servers[index]});
            }
            if (!available.isEmpty() && i < N) {
                System.out.println("free : " + i + " ; "+ Arrays.toString(availableList(available).toArray()));
                int[] freeServer = available.poll();
                running.offer(new int[] {freeServer[0], time + tasks[i]});
                res[i++] = freeServer[0];
            }
            time++;
        }
        return res;
    }

    private List<String> availableList(PriorityQueue<int[]> pq) {
        List<String> res = new ArrayList<>();
        for (int[] p : pq) {
            res.add(p[0] + " - " + p[1]);
        }
        return res;
    }
}
