```
struct Task {
    int id;
    int time_needed;
public:
    Task() {}
    Task(int id, int time_needed) : id(id), time_needed(time_needed) {}
};

struct Server {
    int id;
    int weight;
    int available_time;
public:
    Server() {}
    Server(int id, int weight) : id(id), weight(weight), available_time(-1) {}

    void schedule_task(Task task, int curr_time) {
        available_time = curr_time + task.time_needed;
    }
};

struct WeightComparator {
    bool operator()(Server const& lhs, Server const& rhs) {
        if (lhs.weight == rhs.weight) return lhs.id > rhs.id;
        return (lhs.weight > rhs.weight);
    }
};

struct AvailableComparator {
    bool operator()(Server const& lhs, Server const& rhs) {
        return (lhs.available_time > rhs.available_time);
    }
};

class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        int total_tasks = tasks.size();
        std::vector<int> ans(total_tasks, -1);

        /// Min-Heap of free servers: The server with minimum weight is on the top
        std::priority_queue<Server, std::vector<Server>, WeightComparator> free_servers;

        /// Min-Heap of busy servers: The server with earliest availability is on the top
        std::priority_queue<Server, std::vector<Server>, AvailableComparator> busy_servers;

        /// Task of queue which gets 1 new task at every clock tick
        std::queue<Task> task_queue;

        /// Initialize the free server heap
        for (int i = 0; i < servers.size(); i++) {
            free_servers.push(Server{i, servers[i]});
        }

        /// Simulate task scheduling starting with 0th task at 0th time
        int task_id = 0;
        int curr_time = 0;

        /// While we have tasks in the task queue or tasks to schedule in the task queue
        while (!task_queue.empty() || task_id < total_tasks) {

            /// Peek busy servers heap to find *all* the servers becoming available at the current time
            while (!busy_servers.empty() && busy_servers.top().available_time <= curr_time) {
                Server server = busy_servers.top();  busy_servers.pop();
                free_servers.push(server);
            }       

            /// Process *all* tasks from the task_queue on free servers starting with minimum weight
            while (!task_queue.empty() && !free_servers.empty()) {
                Server server = free_servers.top(); free_servers.pop();
                Task task = task_queue.front(); task_queue.pop();

                /// schedule `task` on the free `server` with minimum weight
                server.schedule_task(task, curr_time);
                busy_servers.push(server);
                ans[task.id] = server.id;
            }

            /// Simluate timeline
            if (task_id < total_tasks) {
                // as long as we iterating over tasks we simulate `1` clock tick at a time
                // because at each clock tick we can only put one task in the queue
                curr_time++;
                // every clock tick put a task in the task queue
                // note that we put one task per clock tick into the queue, but multiple tasks
                // can be scheduled from the the task_queue at a simulated clock tick
                task_queue.push(Task{task_id, tasks[task_id++]});
            } else {
                // once all the tasks are in the task_queue, the next simulation event time stamp
                // is governed by when one of the earliest busy server becomes available
                curr_time = busy_servers.top().available_time;
            }
        }

        return ans;
    }
};
```

