            while (!busy_servers.empty() && busy_servers.top().available_time <= curr_time) {
                Server server = busy_servers.top();  busy_servers.pop();
                free_servers.push(server);
            }       

            /// Process *all* tasks from the task_queue on free servers starting with minimum weight
            while (!task_queue.empty() && !free_servers.empty()) {
                Server server = free_servers.top(); free_servers.pop();
                Task task = task_queue.front(); task_queue.pop();

                /// schedule `task` on the free `server` with minimum weight
                server.schedule_task(task, curr_time);
                busy_servers.push(server);
                ans[task.id] = server.id;
            }

            /// Simluate timeline
            if (task_id < total_tasks) {
                // as long as we iterating over tasks we simulate `1` clock tick at a time
                // because at each clock tick we can only put one task in the queue
                curr_time++;
                // every clock tick put a task in the task queue
                // note that we put one task per clock tick into the queue, but multiple tasks
                // can be scheduled from the the task_queue at a simulated clock tick
                task_queue.push(Task{task_id, tasks[task_id++]});
            } else {
                // once all the tasks are in the task_queue, the next simulation event time stamp
                // is governed by when one of the earliest busy server becomes available
                curr_time = busy_servers.top().available_time;
            }
        }

        return ans;
    }
};
