class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

        vector<int> res;
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>>  avail;  //declaring minHeap

        for(int i=0; i<servers.size(); i++)
            avail.push({servers[i], i});

        priority_queue<array<int, 3>, vector<array<int, 3>>, greater<array<int, 3>>> endTime;   //declaring minHeap

        int i= 0, time= 0;

        while(i< tasks.size())
        {
            //retrieving completed tasks
            while(!endTime.empty() && endTime.top()[0] <= time)
            {
                auto [e, server1, idx] = endTime.top(); endTime.pop();
                avail.push({server1, idx});
            }

            //execute new tasks
            while(i < tasks.size() && i<= time)
            {
                int currStart= tasks[i];

                //if servers are available
                if(!avail.empty())
                {
                    auto [currServer, id] = avail.top(); avail.pop();
                    i++;
                    res.push_back(id);
                    endTime.push({time+currStart, currServer, id});
                }
                //no server available
                else 
                {
                    time = max(time, endTime.top()[0]-1);       // speedup simulation
                    break;
                }
            }
            time++;
        }
        return res;
    }
};

