Based off of the instructional video from: https://leetcode.com/problems/process-tasks-using-servers/discuss/1239821/Two-MinHeaps-Explained
```
public class Solution {
    public int[] AssignTasks(int[] servers, int[] tasks) {
        int[] r = new int[tasks.Length];
        SortedSet<(int wt, int idx)> avail = new SortedSet<(int wt, int idx)>();
        SortedSet<(int tavail, int wt, int idx)> unavail = new SortedSet<(int tavail, int wt, int idx)>();
        for (int i = 0; i < servers.Length; ++i) 
            avail.Add((servers[i], i));

        int t = 0;
        for (int i = 0; i < tasks.Length; ++i) {
            t = Math.Max(t, i);
            if (avail.Count == 0) t = unavail.Min.tavail;

            while (unavail.Count > 0 && t >= unavail.Min.tavail) {
                var min = unavail.Min;
                unavail.Remove(min);
                avail.Add((min.wt, min.idx));
            }

            (int wt, int idx) server = avail.Min;
            avail.Remove(server);
            unavail.Add((t + tasks[i], server.wt, server.idx));
            r[i] = server.idx;
        }

        return r;
    }
}
