class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        PriorityQueue<Server> available = new PriorityQueue<>((s1, s2) -> (s1.weight == s2.weight ? s1.index - s2.index : s1.weight - s2.weight));
        PriorityQueue<Server> unavailable = new PriorityQueue<>((s1, s2) -> (s1.time == s2.time ? s1.weight == s2.weight ? 
                                                                             s1.index - s2.index : s1.weight - s2.weight :
                                                                             s1.time - s2.time));
        for (int i = 0; i < servers.length; i++) {
            available.add(new Server(servers[i], i, 0));
        }

        int[] result = new int[tasks.length];
        for (int i = 0; i < tasks.length; i++) {      
            while (!unavailable.isEmpty() && unavailable.peek().time <= i) {                                                
                available.add(unavailable.poll());
            }
            Server as = available.poll(); // available server
            if (as == null) {
                as = unavailable.poll(); // use the first one from unavailable servers
            }
            as.time = Math.max(i + tasks[i], as.time + tasks[i]); 
            result[i] = as.index;
            unavailable.add(as);
        }
        return result;
    }

    class Server {
        int weight;
        int index;
        int time;

        Server(int weight, int index, int time) {
            this.weight = weight;
            this.index = index;
            this.time = time;
        }
    }
}
