// simulate the problem description , since the description is priority - driven it gives us an intuition to use heaps , and that is the way to solve this problem optimally 

// TC : O(nlogn) 
// sc : O(n)

class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {

        PriorityQueue<FreeServers> fpq = new PriorityQueue(); // min heap of free servers 
        PriorityQueue<RunningServers> rpq = new PriorityQueue(); // min heap of running servers

        for(int i =0;i<servers.length;i++){
            fpq.add(new FreeServers(servers[i],i)); // initially all servers are free
        }

        int time = 0; // current time;
        int ans[] = new int[tasks.length]; // result 

        for(int i =0;i<tasks.length;i++){

            time = Math.max(time,i); // for cases when we wait for running servers to become free

            if(fpq.size()==0){ // no free servers

                RunningServers server = rpq.peek();
                time = server.freeTime; // wait till a server becomes free 

            }
                while(rpq.size()>0&&time>=rpq.peek().freeTime){ // add all running servers which became free to free servers 
                    RunningServers server = rpq.remove();
                    fpq.add(new FreeServers(server.weight,server.idx));

                }

            FreeServers server = fpq.remove(); // get latest free server
            ans[i] = server.idx; // index of server is answer for current task
            int freeTime = time + tasks[i]; // time by which this task will be completed by server
            rpq.add(new RunningServers(freeTime,server.weight,server.idx)); // this server is now a running server till it completes current task , add it to running servers

        }

        return ans;

    }
}
class FreeServers implements Comparable<FreeServers> {
    int weight;
    int idx;

    public FreeServers(int weight , int idx){
        this.weight = weight;
        this.idx = idx;
    }

    public int compareTo(FreeServers other){
        if(this.weight!=other.weight) return this.weight-other.weight;
        return this.idx-other.idx;
    }
}
class RunningServers implements Comparable<RunningServers>{
    int freeTime;
    int weight;
    int idx;

    public RunningServers(int freeTime , int weight , int idx){
        this.freeTime = freeTime;
        this.weight = weight;
        this.idx = idx;
    }

    public int compareTo(RunningServers other){
        if(this.freeTime!=other.freeTime) return this.freeTime-other.freeTime;
        if(this.weight!=other.weight) return this.weight - other.weight;
        return this.idx - other.idx;
    }
}
