class Solution {
public:
    struct cust
    {
        int w;
        int ind;
        bool operator < (const cust & s2)
        const
        {
            return (w > s2.w) || (w==s2.w && ind > s2.ind);
        }
    };
    struct cust1
    {
        int time;
        int ind;
        bool operator < (const cust1 & s2)
        const
        {
            return (time > s2.time);
        }
    };
    vector<int> assignTasks(vector<int>& ser, vector<int>& task) {

        priority_queue<cust>avail;//weight index
        priority_queue<cust1>st;//time index
        vector<int>ans;
        int x = 0;
        for(auto it : ser)
        {
            avail.push({it,x});
            x++;
        }
        int ti = -1;
        for(int i = 0;i<task.size();i++)
        {
            if(ti<i)
                ti = i;
            if(avail.size()==0)
            {
                int id = st.top().ind;
                int tim = st.top().time;
                avail.push({ser[id],id});
                st.pop();
                ti = max(ti,tim);
            }
            while(st.size()>0 && st.top().time<=ti)
            {
                int id = st.top().ind;
                avail.push({ser[id],id});
                st.pop();
            }
            int id = avail.top().ind;
            ans.push_back(id);
            avail.pop();
            st.push({ti + task[i],id});
