```java
class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        int n = servers.length;
        int m = tasks.length;
        int[] res = new int[m];
        //[server index, weight]
        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b) ->{
            if(a[1] != b[1]){
                return a[1] - b[1];
            }
            return a[0] - b[0];
        });
        //[server index, available time]
        PriorityQueue<int[]> used = new PriorityQueue<>((a,b) ->{
            return a[1] - b[1];
        });
        for(int i = 0; i < n; i++){
            pq.add(new int[]{i, servers[i]});
        }

        //processing
        int i = 0;
        int curTime = 0;
        while(i < m){
            //get more available servers
            while(!used.isEmpty() && used.peek()[1] <= curTime){
                int[] out = used.poll();
                pq.offer(new int[]{out[0], servers[out[0]]});
            }
            //has available server
            while(i < m && i <= curTime && !pq.isEmpty()){
                int[] use = pq.poll();
                res[i] = use[0];
                used.offer(new int[]{use[0], curTime + tasks[i]});
                i++;
            }
            //jump to next
            if(pq.isEmpty()){
                curTime = used.peek()[1];
            }
            //move forward by second
            else{
                curTime++;
            }
        }
        return res;
    }
}
