class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        auto comp_weight = [&](const Server &s1, const Server &s2)
        {
            if(servers[s1.index] == servers[s2.index])
            {
                return s1.index > s2.index;
            }

            return servers[s1.index] > servers[s2.index];
        };

        auto comp_time = [](const Server &s1, const Server &s2)
        {
            if(s1.time == s2.time)
            {
                return s1.index > s2.index;
            }
            return s1.time > s2.time;
        };


        priority_queue<Server, vector<Server>, decltype(comp_weight)> available(comp_weight);
        priority_queue<Server, vector<Server>, decltype(comp_time)> unavailable(comp_time);

        for(int i = 0 ; i < servers.size(); i++)
        {
            available.push({i, 0});
        }

        vector<int> ans;
        int t = 0;
        for(int i = 0 ; i < tasks.size() ; i++, t++)
        {
            if(available.empty() && unavailable.top().time > t)
            {
                t = unavailable.top().time;
            }

            while(!unavailable.empty() && unavailable.top().time <= t)
            {
                auto topServer = unavailable.top();
                unavailable.pop();
                available.push(topServer);
            }

            auto topServer = available.top();
            available.pop();
            ans.push_back(topServer.index);
            unavailable.push({topServer.index, t+tasks[i]});
            // cout << "index " << index << endl;
        }
        return ans;
    }
};
