class Solution {
public:
    typedef pair<int,int> pii;
    typedef pair<int,pii> pipii;

	//Comparator for comparing Free Servers in Servers with Minimum Weight and when same weights on indexex
    struct myCompares
    {
        bool operator()(pii& p1, pii& p2)
        {
            if(p1.first==p2.first)
            {
                return p1.second>p2.second;
            }
            return p1.first>p2.first;
        }
    };

    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

        priority_queue<pii,vector<pii>,myCompares> Servers;   **//Free Servers**
	priority_queue<pipii,vector<pipii>,greater<pipii>> usedServers;  **//Used Servers**

        for(int i=0;i<servers.size();i++)
        {
		// Initially Push all servers in Free Server Min Heap
            Servers.push({servers[i],i});
        }

        vector<int>ans;
        int time=0; //Keep a time counter to keep track of time of processes

        for(int i=0;i<tasks.size();) //Now Until all tasks are done run this loop
        {

		//check at this point if at this time instance any servers are freed or not from used servers
            while(!usedServers.empty() && usedServers.top().first<=time)
            {
                Servers.push({usedServers.top().second.first,usedServers.top().second.second});
                usedServers.pop();
            }

            if(!Servers.empty())
            {  //if servers are not empty and time is greater than the assinged tasks indexes then assign all Tasks until that point to all the servers available at the same time
                while(!Servers.empty() && i<=time && i<tasks.size())
                {

                    pii serv=Servers.top();Servers.pop();
                    usedServers.push({time+tasks[i],{serv.first,serv.second}});
                    ans.push_back(serv.second);
                    i++;
                }
                time++;
            }
            else time=usedServers.top().first; //if no servers are availble go to time of used servers top elements finising time
        }
        return ans;
    }
};
