class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> freePq, busyPq;

        int n = servers.size();
        for (int i = 0; i < n; ++i) freePq.push({servers[i], i});

