  class Solution {
public:
    struct myComp {
    constexpr bool operator()(
        pair<int, int> const& a,
        pair<int, int> const& b)
        const noexcept
    {
       if(a.first == b.first){
           return a.second > b.second;
       }
        return a.first > b.first;
    }
};


    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

//         two pqs
        priority_queue< pair<int,int> , vector<pair<int,int>> , myComp > pq1,pq2;

//         this queue we are using if all servers are busy then serve them in proper order.
        queue<int> q;

//         build server pq
        for(int i = 0; i < servers.size(); i++){
            pq1.push({servers[i],i});
        }


        vector<int> ans;

        int m = tasks.size(),sec = 0;

//         
        for(int i = 0; i < m; i++){

//             check pq2 if any tasks are processed and can push back to pq1
            while(!pq2.empty() && pq2.top().first == sec){
                pair<int,int> tmp = pq2.top();
                pq2.pop();

                pq1.push({servers[tmp.second],tmp.second});
            }


            q.push(i);

//             check pq1 if any servers are available and use them to process our tasks
            while(!q.empty() && !pq1.empty()){
                pair<int,int> curr = pq1.top();
                pq1.pop();

                int ind = q.front();
                q.pop();

                ans.push_back(curr.second);
                pq2.push({tasks[ind]+sec,curr.second});
            }

            sec++;
        }

//         this if any tasks still in queue, then process them
        while(!q.empty()){
            if(pq1.empty()){
                sec = pq2.top().first;
            }

             while(!pq2.empty() && pq2.top().first == sec){
                pair<int,int> tmp = pq2.top();
                pq2.pop();

                pq1.push({servers[tmp.second],tmp.second});
            }

             while(!q.empty() && !pq1.empty()){
                pair<int,int> curr = pq1.top();
                pq1.pop();

                int ind = q.front();
                q.pop();

                ans.push_back(curr.second);
                pq2.push({tasks[ind]+sec,curr.second});
            }

            sec++;


        }

        return ans;


    }
};
