#define ll long long
#define ff first
#define ss second
#define pb push_back

class Solution {
public:
    vector<int> assignTasks(vector<int>& s, vector<int>& t) {
        // min server weight // index;
        // This queue is for servers that are free 
        priority_queue <pair<int,int>, vector<pair<int,int>>, greater< pair<int,int>>> free;

        //freetime // weight //index
         // This queue is for servers that are assigned to tasks
        priority_queue <pair<int,pair<int,int>>, vector<pair<int,pair<int,int>>>, greater< pair<int,pair<int,int>>>> unfree;


        for(int i=0;i<s.size();i++){
            free.push({s[i] , i});
        }

        int time = 0 ;
        int n = t.size();
        vector<int>ans(n);

        for(int i=0;i<n;i++){


            // Taking out the server that became free till that time
            if(!unfree.empty()){ 
                while(!unfree.empty()){
                    pair<int,pair<int,int>> p = unfree.top();
                    if(p.ff <=time){
                        unfree.pop();
                        free.push({p.ss.ff , p.ss.ss});
                    }else{
                        break;
                    }
                }
            }

            // If no servers available , then wait till that time
            if(free.empty()){

                pair<int,pair<int,int>> p = unfree.top();
                unfree.pop();
                ans[i] = p.ss.ss;
                unfree.push({p.ff + t[i],{p.ss.ff , p.ss.ss}});
                time++ ;
                continue;
            }

            // else assign the free server
            pair<int,int> x = free.top();
            free.pop();
            ans[i] = x.ss;
            unfree.push({time+t[i],{x.ff , x.ss} });

            time++;
        }
        return ans;
    }
};

