class Solution {
    public int[] assignTasks(int[] s, int[] t) {
        PriorityQueue<int[]> sf = new PriorityQueue<>((a,b) -> a[0] == b[0] ? Integer.compare(a[1],b[1]):Integer.compare(a[0],b[0]));//free server with least weight first
        for(int i = 0;i<s.length;i++)
            sf.offer(new int[]{s[i],i,0});//initially every server is free
        int cs = 0;//current second
        PriorityQueue<int[]> su = new PriorityQueue<>((a,b) -> Integer.compare(a[2],b[2]));//server which is being used sorted with their free time
        int res[] = new int[t.length];
        for(int i = 0;i<t.length;){
            if(sf.size() == 0) cs = su.peek()[2];//if no server is free skip to the nearest time when a server will be free
            while(!su.isEmpty() && su.peek()[2] <= cs) sf.offer(su.poll());//every server which is free till current time add that to free server queue
            while(sf.size() != 0 && i<t.length && i<=cs){//assign server to every task which is ready to process at current time
                int[] temp = sf.poll();//remove a free server from the queue
                res[i] = temp[1];//stored the server assigned in result
                temp[2] = cs+t[i];//set the time when the this server will be free and add it to the queue of used server
                su.offer(temp);
                i++;
            }
            cs++;
        }
        return res;
    }
}
