class Server {
  int index;
  int weight;
  int availableTime;
  Server(int index, int weight) {
    this.index = index;
    this.weight = weight;
  }
}
class Solution {
  public int[] assignTasks(int[] servers, int[] tasks) {
    // sort free servers by weight ASC, index ASC
    PriorityQueue<Server> freeServers = new PriorityQueue<>((a,b) -> a.weight != b.weight ? Integer.compare(a.weight, b.weight) : Integer.compare(a.index, b.index));

    for (int i = 0; i < servers.length; i++)
      freeServers.add(new Server(i, servers[i]));

    int[] result = new int[tasks.length];
    // sort busy servers by availableTime ASC, weight ASC, index ASC
    PriorityQueue<Server> busyServers = new PriorityQueue<>((a,b) -> {
      if (a.availableTime != b.availableTime)
        return Integer.compare(a.availableTime, b.availableTime);
      else if (a.weight != b.weight)
        return Integer.compare(a.weight, b.weight);
      else
        return Integer.compare(a.index, b.index);
      });
    for (int time = 0; time < tasks.length; time++) {
      while (!busyServers.isEmpty() && busyServers.peek().availableTime <= time)
        freeServers.add(busyServers.remove());

      if (!freeServers.isEmpty()) {
        // if there are one or more free servers take the one with the smallest weight, index
        Server server = freeServers.remove();                
        result[time] = server.index;
        server.availableTime = time + tasks[time];
        busyServers.add(server);
      } else {
        // if there are no free servers, take the busy server with the smallest available time, weight, index
        Server server = busyServers.remove();                
        result[time] = server.index;
		// set the server's new available time based on its current available time
        server.availableTime += tasks[time];
        busyServers.add(server);
      }
    } 
    return result;
  }
}
