class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        TreeMap<Integer, Queue<Integer>> timeStamp = new TreeMap();
        Queue<Integer> queueTasks = new LinkedList();
        PriorityQueue<int[]> pq = new PriorityQueue<int[]>(new Comparator<int[]>(){
            @Override
            public int compare(int[] a, int[] b)
            {
                if(a[1] == b[1])
                    return a[0] - b[0];

                return a[1] - b[1];
            }
        });

        for(int i=0; i<servers.length; i++)
            pq.add(new int[]{i, servers[i]});

        int[] res= new int[tasks.length];
        int taskProcessingTimeIndex = 0;
        int time=0;
        queueTasks.add(tasks[0]);
        while(!queueTasks.isEmpty()) {

            // Get servers that need to be freed
            var q = timeStamp.get(time);

            // Free servers
            while(q != null && !q.isEmpty()) {         
                var item = q.poll();
                pq.add(new int[]{-item, servers[-item]});
            }

			// Remove time entry
            if(q != null)
                timeStamp.remove(time);

            // All servers busy
            if(pq.isEmpty()) {

                // Add next task
                if(++time<tasks.length)
                    queueTasks.add(tasks[time]);
                else // If time has past tasks length, get the next time when a server is available
                    time = timeStamp.firstKey();

                continue;
            }

            // Allocate servers for queued tasks
            while(!pq.isEmpty() && !queueTasks.isEmpty()) {                
                 var taskTime = queueTasks.poll();
                 var server = pq.poll();
                 res[taskProcessingTimeIndex++] = server[0];

                if(!timeStamp.containsKey(time+taskTime))
                    timeStamp.put(time+taskTime, new LinkedList());

                // Log the time when the server used gets free
                timeStamp.get(time+taskTime).add(-server[0]);
            }

            // Add next task
            if(++time<tasks.length)
                queueTasks.add(tasks[time]);
            else // If time has past tasks length, get the next time when a server is available
                time = timeStamp.firstKey();
        }

        return res;
    }
}
