	vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

        // Defined struct WI and TI with their respective comparators already
        // Min priority queues : Free - {weight, idx}, Busy - {time, idx}
        priority_queue<WI, vector<WI>, CompareWI> Free;
        priority_queue<TI, vector<TI>, CompareTI> Busy;

        // Initalizing Free with all servers
        for(int i=0;i<servers.size();i++)
            Free.push( WI(servers[i], i) );

        // Array to store Assignments
        vector<int> Assign(tasks.size());

        int time = 0;

        // First, the first server will be popped from Free and Task 0 will be assigned to it
        // so it will be pushed to Busy.
        // Then, we will increment time by 1 and pop elements from Busy whose tasks are done
        // and push them back into Free. Then we again pick from Free and so on... 
        // When all the servers become Busy, we jump our time from now to the time
        // when the next server is free (with lowest end time).

        // To handle the case of assigning multiple tasks, we will not increment time when
        // we popped more that one element from Busy, So we increment time only after we assign
        // all the tasks to servers available . 

        for(int i = 0; i < tasks.size(); i++){

            if(Free.empty()) time = Busy.top().time;

            while(!Busy.empty()&&Busy.top().time<=time){

                Free.push( WI( servers[Busy.top().idx], Busy.top().idx ) );
                Busy.pop();

            }

            Assign[i] = Free.top().idx;
            Busy.push( TI( time + tasks[i], Free.top().idx ) );
            Free.pop();

            if(time<=i)
                time++;

        }

        return Assign;

    }

