typedef pair<int, int> pi;

class Solution {
public:
    vector<int> assignTasks(vector<int>& s, vector<int>& t) {

        int n = s.size();
        int m = t.size();

        int timer = 0;

        queue<int>q;
        priority_queue<pi, vector<pi>, greater<pi> > available;
        priority_queue <pi, vector<pi>, greater<pi>> free;

        for(int i = 0 ; i < n ; i++){

            available.push(make_pair(s[i] , i));
        }

        vector<int>ans;

        pi use = available.top();
        available.pop();
        free.push(make_pair(t[0] , use.second));
        ans.push_back(use.second);

        timer = 1;

        while(ans.size() < m){

            if(timer < m) q.push(timer);

            while( !free.empty() && timer >= free.top().first){

                pi temp = free.top();
                free.pop();
                int server_index = temp.second;
                available.push(make_pair(s[server_index] , server_index));
            }


            while( !available.empty() && !q.empty() ){

                int task = q.front();
                q.pop();

                pi temp = available.top();
                available.pop();

                ans.push_back(temp.second);
                free.push(make_pair(timer+t[task],temp.second));
            }

            if(timer >= m) timer += free.top().first-timer;
            else timer++;
        }



        return ans;

    }
};
