use std::cmp::*;
use std::collections::*;

impl PartialEq for Wrapper {
  fn eq(&self, other: &Wrapper) -> bool {
    self.0 == other.0
  }
}

impl PartialOrd for Wrapper {
  fn partial_cmp(&self, other: &Wrapper) -> Option<Ordering> {
    let v = self.0.cmp(&other.0);
    Some(if v == Ordering::Equal {
      self.1.cmp(&other.1)
    } else {
      v
    })
  }
}

#[derive(Clone, Copy, Ord, Eq, Debug)] 
struct Wrapper(isize,usize);
impl Solution {
  pub fn assign_tasks(servers: Vec<i32>, tasks: Vec<i32>) -> Vec<i32> {
    let mut btree_set = BTreeSet::new();
    let n = servers.len();
    let m = tasks.len();
    let mut result = vec![0;m];
    for i in 0..n {
      btree_set.insert(Wrapper(servers[i] as isize,i));
    }

    let mut time = 0;
    let mut heap = BinaryHeap::new();

    for (i, v) in tasks.into_iter().enumerate() { 
      time = i.max(time);
      if btree_set.is_empty() {
        let Reverse((ct, index)) = heap.pop().unwrap();
        time = ct;
        btree_set.insert(Wrapper(servers[index] as isize, index));
      }

      while let Some(Reverse((ct, index))) = heap.pop() {
        if time < ct {
          heap.push(Reverse((ct, index)));
          break
        }
        btree_set.insert(Wrapper(servers[index] as isize, index));
      }

      let &Wrapper(wait, index) = btree_set.iter().next().unwrap();
      btree_set.remove(&Wrapper(wait, index));
      result[i] = index as i32;

      let nt = time+v as usize;
      heap.push(Reverse((nt, index)));
    }

    result
  }
}
