class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        vector<int> res;
        priority_queue<pair<int,int>, vector<pair<int,int>>, greater<pair<int,int>>> avail; 
        // this will keep track of available servers

        // pushing servers in available heap
        for(int i = 0; i < servers.size(); i++) avail.push({servers[i], i});

        priority_queue<array<int, 3>, vector<array<int, 3>>, greater<array<int, 3>>> unavail; 
        // (timeServerBecomesFree, serverWeight, ind) 
        // this will keep track of unavailable servers

