class Solution {
    private class FreeServComparator implements Comparator<int[]> {
        @Override
        public int compare(int[] a, int[] b) {
            if(a[0] == b[0]) {
                return a[1] - b[1];
            }
            return a[0] - b[0];
        }
    }

    private class RunningServComparator implements Comparator<int[]> {
        @Override
        public int compare(int[] a, int[] b) {
            return a[0] - b[0];
        }
    }

    public int[] assignTasks(int[] servers, int[] tasks) {
        PriorityQueue<int[]> free = new PriorityQueue<>(new FreeServComparator());
        PriorityQueue<int[]> running = new PriorityQueue<>(new RunningServComparator());

        for(int i = 0; i < servers.length; i++) {
            free.offer(new int[]{servers[i], i});
        }

        int[] ans = new int[tasks.length];
        int currTime = 0;
        for(int j = 0; j < tasks.length; j++) {
            currTime = Math.max(j, currTime);
            if(free.isEmpty()) {
                currTime = (running.peek())[0];
            }
            while(!running.isEmpty() && currTime == (running.peek())[0]) {
                int[] temp = running.poll();
                int[] freeServ = new int[]{servers[temp[1]], temp[1]};
                free.offer(freeServ);
            }
            int[] serv = free.poll();
            ans[j] = serv[1];
            running.offer(new int[]{currTime + tasks[j], serv[1]});
        }

        return ans;
    }
}
