class Solution {
 public:
  vector<int> assignTasks(const vector<int> &servers, const vector<int> &tasks) {
    using pq_busy_servers_node = pair<int, int>;   // {the available time, the server index}
    const int n_servers = static_cast<int>(servers.size());
    const int n_tasks = static_cast<int>(tasks.size());
    auto compare_available_servers = [&servers](const int lhs, const int rhs) -> bool {
      return !(servers[lhs] < servers[rhs] || (servers[lhs] == servers[rhs] && lhs < rhs));
    };
    priority_queue<int, vector<int>, decltype(compare_available_servers)> pq_available_servers(compare_available_servers);
    priority_queue<pq_busy_servers_node, vector<pq_busy_servers_node>, greater<>> pq_busy_servers;
    for (int i_server = 0; i_server < n_servers; ++i_server) {
      pq_available_servers.emplace(i_server);
    }

    int current_time = numeric_limits<int>::min();
    vector<int> ret(n_tasks);
    for (int i_task = 0; i_task < n_tasks;) {
      if (current_time < i_task) {
        current_time = i_task;
      }

      while (!pq_busy_servers.empty() && pq_busy_servers.top().first <= current_time) {
        const auto [_, server_index] = pq_busy_servers.top();
        pq_busy_servers.pop();
        pq_available_servers.emplace(server_index);
