class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        int n = servers.size();
        int m = tasks.size();
        vector<int> ans(m, -1);
        auto comp = [](pair<int, int>& p1, pair<int, int>& p2) {
            if (p1.first != p2.first) {
                return p1.first > p2.first;
            }
            return p1.second > p2.second;
        };
        priority_queue<pair<int, int>, vector<pair<int, int>>, decltype(comp)> pq1(comp);  // {weight, index}
        priority_queue<pair<int, int>, vector<pair<int, int>>, decltype(comp)> pq2(comp);  // {avail_time, index}
        for (int i = 0; i < n; ++i) {
            pq1.push({servers[i], i});
        }
        int t = 0;
        int j = 0;
        while (j < m) {
            if (pq1.empty()) {
                t = pq2.top().first;  // skip waiting time
            }
            while (!pq2.empty() && pq2.top().first <= t) {
                pq1.push({servers[pq2.top().second], pq2.top().second});
                pq2.pop();
            }
            while (!pq1.empty() && j < m && j <= t) {
                pq2.push({t + tasks[j], pq1.top().second});
                ans[j] = pq1.top().second;
                pq1.pop();
                j++;
            }
            t++;
        }
        return ans;
    }
};
