class Solution {
    class FreeServer{
        public int serverIdx;
        public int serverWeight;
        public FreeServer(int serverIdx,int serverWeight){
            this.serverIdx=serverIdx;
            this.serverWeight=serverWeight;
        }
    }
    class ServerTime{
        public int serverIdx;
        public int endTime;
        public ServerTime(int serverIdx,int endTime){
            this.serverIdx=serverIdx;
            this.endTime=endTime;
        }
    }
    public int[] assignTasks(int[] servers, int[] tasks) {
        int n=tasks.length;
        int [] ans = new int[n];
        PriorityQueue<ServerTime> pqServerTime= new PriorityQueue<>((s1,s2)->Integer.compare(s1.endTime,s2.endTime));
        PriorityQueue<FreeServer> pqFreeServer=new PriorityQueue<>((s1,s2)->{
            if(s1.serverWeight!=s2.serverWeight)
                return Integer.compare(s1.serverWeight,s2.serverWeight);
            else
                return Integer.compare(s1.serverIdx,s2.serverIdx);
        });


        for(int i=0;i<servers.length;i++)
            pqFreeServer.add(new FreeServer(i,servers[i]));

        int ctime=0;
        int lp=0;

        while(lp!=tasks.length){
            while(pqServerTime.size()!=0 && pqServerTime.peek().endTime<=ctime){
               ServerTime tmp= pqServerTime.remove();
               int serverWeight = servers[tmp.serverIdx];
               pqFreeServer.add(new FreeServer(tmp.serverIdx,serverWeight));
            }
            if(pqFreeServer.size()==0){
                  ctime=pqServerTime.peek().endTime;
                  continue;
            }
            FreeServer fs=pqFreeServer.remove();
            ans[lp]=fs.serverIdx;
            pqServerTime.add(new ServerTime(ans[lp],ctime+tasks[lp]));
            lp++;
            ctime=Integer.max(ctime,lp);
        }
        return ans;
    }
}
