class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        // int[0] = weight, int[1] = index.
        PriorityQueue<int[]> ava = new PriorityQueue<>((a, b) -> {
            if (a[0] == b[0]) {
                return a[1] - b[1];
            } else {
                return a[0] - b[0];
            }
        });
        // int[0] = available time, int[1] = index.
        PriorityQueue<int[]> pq = new PriorityQueue<>((a, b) -> a[0] - b[0]);
        for (int i = 0; i < servers.length; i++) {
            ava.offer(new int[]{servers[i], i});
        }

        int[] res = new int[tasks.length];
        int index = 0, time = 0;
        while (index < tasks.length) {
            while (!pq.isEmpty() && pq.peek()[0] <= time) {
                int[] s = pq.poll();
                ava.offer(new int[]{servers[s[1]], s[1]});
            }
            boolean pick = false;
            while (!ava.isEmpty() && index < time && index < tasks.length) {
                int[] cur = ava.poll();
                pq.offer(new int[]{time + tasks[index], cur[1]});
                res[index++] = cur[1];
                pick = true;
            }
            if (!pick && !pq.isEmpty()) {
                time = pq.peek()[0];
            } else {
                time++;
            }
        }
        return res;
    }
}
