class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        int[] res = new int[tasks.length];
        PriorityQueue<int[]> occupied  = new PriorityQueue<>(Comparator.comparingInt(a -> a[1]));
        PriorityQueue<Integer> avail = new PriorityQueue<>((a,b) -> {
            if(servers[a] == servers[b])
                return a-b;
            return servers[a] - servers[b];
        });
        for (int i = 0; i < servers.length; i++)
            avail.offer(i);

        for (int time = 0; time < tasks.length; time++){
            int currTime = time;
            while (!occupied.isEmpty() && occupied.peek()[1] <= currTime){
                int[] server = occupied.poll();
                int idx = server[0];
                avail.offer(idx);
            }
            if(avail.size() > 0){
                int currServerIndex = avail.poll();
                occupied.offer(new int[]{currServerIndex, time+ tasks[time]});
                res[time] = currServerIndex;
            }else{
                int[] occupiedServer = occupied.poll();
                occupied.offer(new int[]{occupiedServer[0], (occupiedServer[1]-time)+ tasks[time]});
                res[time] = occupiedServer[0];
            }
        }
        return res;
    }
}
