class Solution {

    class Server{

        int weight;
        int ind;
        int orderOfInsertion;

        public Server(int weight, int ind){
            this.weight = weight;
            this.ind = ind;
        }


    }

    class Task{

        Server s;
        int timeStart;
        int duration;
        int ind;

        public Task(Server s, int start, int duration, int ind){
            this.s = s;
            this.timeStart = start;
            this.duration = duration;
            this.ind = ind;
        }

    }

    class ServerComparator1 implements Comparator<Server>{

        public int compare(Server s1, Server s2){

            if(s1.weight == s2.weight) return s1.ind - s2.ind;
            return s1.weight - s2.weight;

        }


    }

    class TaskComparator implements Comparator<Task>{

        public int compare(Task t1, Task t2){

            if(t1.timeStart + t1.duration == t2.timeStart + t2.duration){
                return t1.ind - t2.ind;
            }

            return (t1.timeStart + t1.duration) - (t2.timeStart + t2.duration);


        }

    }

    public int[] assignTasks(int[] servers, int[] tasks) {

        int[] ans = new int[tasks.length];
        PriorityQueue<Server> pq = new PriorityQueue<Server>( new ServerComparator1());
        PriorityQueue<Task> taskQ = new PriorityQueue(new TaskComparator());
        int taskInd = 0;
        int seconds = 0;

        for(int i = 0; i < servers.length; i++){
            pq.offer(new Server(servers[i], i));
        }

        while(taskInd < tasks.length){           

            while(!taskQ.isEmpty() && taskQ.peek().timeStart + taskQ.peek().duration <= seconds){
                // System.out.println("========removing tasks==========");
                // System.out.println(taskQ.peek() + " Time:" + seconds);
                Task t = taskQ.poll();
                pq.offer(t.s);
            }

            if(pq.isEmpty()){
                seconds = taskQ.peek().timeStart + taskQ.peek().duration;
                continue;
            }

            while(!pq.isEmpty() && taskInd < seconds && taskInd < tasks.length){
                // System.out.println("========add tasks==========");
                Server s = pq.poll();
                Task t = new Task(s, seconds, tasks[taskInd], taskInd);
                // System.out.println(t + " Time:" + seconds);

                taskQ.offer(t);
                ans[taskInd] = s.ind;
                taskInd++;
            }

            seconds++;

        }

        return ans;


    }
}
