            while(!taskQ.isEmpty() && taskQ.peek().timeStart + taskQ.peek().duration <= seconds){
                // System.out.println("========removing tasks==========");
                // System.out.println(taskQ.peek() + " Time:" + seconds);
                Task t = taskQ.poll();
                pq.offer(t.s);
            }

            if(pq.isEmpty()){
                seconds = taskQ.peek().timeStart + taskQ.peek().duration;
                continue;
            }

            while(!pq.isEmpty() && taskInd < seconds && taskInd < tasks.length){
                // System.out.println("========add tasks==========");
                Server s = pq.poll();
                Task t = new Task(s, seconds, tasks[taskInd], taskInd);
                // System.out.println(t + " Time:" + seconds);

                taskQ.offer(t);
                ans[taskInd] = s.ind;
                taskInd++;
            }

            seconds++;

        }

        return ans;


    }
}
