    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        int n = servers.size();
        int m = tasks.size();
        vector<int> res(m, 0);
        list<int> avail_task{};
        map<int, set<int>> free_servers{}; // weight : set of server indicies
        map<int, vector<int>> busy_servers{}; // next_free_time : set of server indicies
        for (int j = 0; j < n; j++) // place all servers as free
        {
            free_servers[servers[j]].emplace(j); 
        }

        for(int i = 0; i < m; i++) 
        {
            if (i < m) { avail_task.emplace_back(i); }

            if (busy_servers.count(i) > 0)
            {
                for (const int _j : busy_servers[i])
                {
                    free_servers[servers[_j]].emplace(_j);
                }
                busy_servers.erase(i);
            }

            while (!avail_task.empty() && !free_servers.empty())
            {
                int i_next = avail_task.front(); avail_task.pop_front();
                int j = *(free_servers.begin()->second.begin());
                decrement_free(free_servers, free_servers.begin()->first, j);
                res[i_next] = j;
                int next_free_time = i + tasks[i_next];
                busy_servers[next_free_time].emplace_back(j);
            }
        }

        while (!avail_task.empty())
        {
            int t = busy_servers.begin()->first;
            for (const int _j : busy_servers[t])
            {
                free_servers[servers[_j]].emplace(_j);
            }
            busy_servers.erase(t);
