package main

import (
	"container/heap"
)

// Heap
type MyHeap [][2]int

func (h MyHeap) Len() int { return len(h) }
func (h MyHeap) Less(i, j int) bool {
	if h[i][0] == h[j][0] {
		return h[i][1] < h[j][1]
	} else {
		return h[i][0] < h[j][0]
	}
}
func (h MyHeap) Swap(i, j int) { h[i], h[j] = h[j], h[i] }

func (h *MyHeap) Push(x interface{}) {
	// Push and Pop use pointer receivers because they modify the slice's length,
	// not just its contents.
	*h = append(*h, x.([2]int))
}

func (h *MyHeap) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}

func (h MyHeap) Top() interface{} {
	return h[0]
}
func assignTasks(servers []int, tasks []int) []int {
	mTask := map[int]int{}
	res := []int{}

	readyH := &MyHeap{}
	runningH := &MyHeap{}

	for i := 0; i < len(servers); i++ {
		mTask[i] = servers[i]
		heap.Push(readyH, [2]int{servers[i], i})
	}

	i, time := 0, 0

	for runningH.Len() > 0 || i < len(tasks) {
		// utilize resource when process is done
		for runningH.Len() > 0 && runningH.Top().([2]int)[0] <= time {
			top := heap.Pop(runningH).([2]int)
			heap.Push(readyH, [2]int{mTask[top[1]], top[1]})
			time = top[0]
		}

		// assigned task to servers
		for readyH.Len() > 0 && i < len(tasks) && time >= i {
			assignedSvr := heap.Pop(readyH).([2]int)
			res = append(res, assignedSvr[1])
			heap.Push(runningH, [2]int{time + tasks[i], assignedSvr[1]})
			i++
		}

		// increase timer
		if readyH.Len() == 0 && runningH.Len() > 0 {
			time = runningH.Top().([2]int)[0]
		} else {
			time++
		}
	}

	return res
}

