#define pq priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> 
#define mk make_pair 
class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

        pq workingServers , freeServers ;

        queue<int> waiting ; 

        vector<int> temp(servers.size(),-1),ans(tasks.size());

        for(int i=0;i<servers.size();i++)  freeServers.push(mk(servers[i],i));

        int time =0 ,iter =0 ;

        while( time<tasks.size() || !waiting.empty() ){

            // fast forward time  
            if(time>=tasks.size()) time = workingServers.top().first ;

            // remove idle server from free servers 
            while(!workingServers.empty() && workingServers.top().first==time) {

                pair<int,int> top = workingServers.top();

                freeServers.push(mk( servers[top.second], top.second ));

                workingServers.pop();
            }
            // push it to queue 
            if(iter<tasks.size()) waiting.push(iter++);

            // see if any servers are free and assign to unfinieshed  tasks!

            while(!freeServers.empty() && !waiting.empty()  ){

                int taskNo = waiting.front();  waiting.pop();

                int allotedServer = freeServers.top().second ; freeServers.pop();

                ans[taskNo] = allotedServer;

                workingServers.push(mk( time+tasks[taskNo] ,allotedServer ));

            }
            time++ ;

        }


        return ans ;

    }
};
